"use strict";function menuHandler(){var e=$(".header__button"),o=$(".header__nav");e.on("click",function(){o.slideToggle()})}function stickyHeader(){$(window).on("scroll",function(){150<$(window).scrollTop()?$("#header").addClass("header--color"):$("#header").removeClass("header--color")})}function smoothScroll(){document.querySelectorAll('a[href^="#"]').forEach(function(e){e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})})}$(document).ready(function(){menuHandler(),stickyHeader(),smoothScroll()});